// Dashboard.jsx
import React, { useState, useEffect } from 'react';
import { Grid, Typography, backdropClasses } from '@material-ui/core';
import styled from 'styled-components'; // Import styled from styled-components
import { gridSpacing } from '../../store/constant';
import Admission from '../admission/Admission.jsx';
import TotalChartCard from '../../ui-component/cards/TotalChartCard/index.js';
import { useMediaQuery } from 'react-responsive';
// Define your styled component
const StyledGrid = {

height: "100%",
width:"100%",
backgroundColor:"white",
position:"absolute",
zIndex:"1000",
top:"80px",
left:"280px",

}
const SmallScreen = {

  height: "200%",
  width:"640px",
  backgroundColor:"white",
position:"relative",
  zIndex:"1000",
  left:'-200px',
  marginLeft:"200px"
  }
  const Mediumscreen = {

    height: "200%",
    width:"700px",
    backgroundColor:"white",
    position:"relative",
    left:'-200px',
    zIndex:"1000",


    }

const Dashboard = () => {
  const [greeting, setGreeting] = useState('');
const[smallScreen,setSmallScreen]=useState(StyledGrid)

useEffect(()=>{
  if(window.innerWidth>957){
    setSmallScreen(StyledGrid)}
    else if(window.innerWidth<957&&window.innerWidth>640)
      {
        setSmallScreen(Mediumscreen)
      }
      else{
        setSmallScreen(SmallScreen)
      }
},[window.innerWidth])

  useEffect(() => {
    const currentHour = new Date().getHours();
    setGreeting(getGreeting(currentHour));
  }, []);

  const getGreeting = (hour) => {
    if (hour >= 5 && hour < 12) {
      return 'Good Morning Admin';
    } else if (hour >= 12 && hour < 18) {
      return 'Good Afternoon Admin';
    } else {
      return 'Good Evening Admin';
    }
  };

  return (
    <Grid container style={smallScreen}>
         <Box
                        component="span"
                        sx={{
                            display: 'flex',
                            maxWidth: 770,
                            minHeight: '100%',
                            height: '100vh',
                            bgcolor: '#fff',
                            backgroundImage: `url(${AuthPattern})`,
                            position: 'relative',
                            backgroundPosition: '0 0',
                            overflow: 'hidden',
                            margin: '0 0 0 auto'
                        }}
                    >

      <Grid item xs={12}>
        <Typography variant="h1" gutterBottom>
          {greeting}
        </Typography>
        <TotalChartCard/>
      </Grid>
      </Box>
    </Grid>
  );
};

export default Dashboard;
