    height: "200%",
    width:"700px",
    backgroundColor:"white",
    position:"relative",
    left:'-200px',
    zIndex:"1000",


    }

const Dashboard = () => {
  const [greeting, setGreeting] = useState('');
const[smallScreen,setSmallScreen]=useState(StyledGrid)

useEffect(()=>{
  if(window.innerWidth>957){
    setSmallScreen(StyledGrid)}
    else if(window.innerWidth<957&&window.innerWidth>640)
      {
        setSmallScreen(Mediumscreen)
      }
      else{
        setSmallScreen(SmallScreen)
      }
},[window.innerWidth])

  useEffect(() => {
    const currentHour = new Date().getHours();
    setGreeting(getGreeting(currentHour));
  }, []);

  const getGreeting = (hour) => {
    if (hour >= 5 && hour < 12) {
      return 'Good Morning Admin';
    } else if (hour >= 12 && hour < 18) {
      return 'Good Afternoon Admin';
    } else {
      return 'Good Evening Admin';
    }
  };

  return (
    <Hidden mdDown>
                <Grid item md={5} sx={{alignSelf: 'stretch'}}>
                    <Box
                        component="span"
                        sx={{
                            display: 'flex',
                            maxWidth: 770,
                            minHeight: '100%',
                            height: '100vh',
                            bgcolor: '#fff',
                            backgroundImage: `url(${AuthPattern})`,
                            position: 'relative',
                            backgroundPosition: '0 0',
                            overflow: 'hidden',
                            margin: '0 0 0 auto'
                        }}
                    >

                    </Box>
                </Grid>
            </Hidden>
  );
};

export default Dashboard;
