import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Button, TextField, Container, Typography, Grid, Paper, Card, CardContent } from '@material-ui/core';

const Tobbar = () => {
  const [tobbar, setTobbar] = useState([]);
  const [number, setNumber] = useState('');
  const [location, setLocation] = useState('');
  const [schoolOpenTiming, setSchoolOpenTiming] = useState('');
  const [editingEntryId, setEditingEntryId] = useState(null);

  useEffect(() => {
    fetchTobbar();
  }, []);

  const fetchTobbar = () => {
    axios.get('http://localhost:5678/tobbar')
      .then(response => setTobbar(response.data))
      .catch(error => console.error('Error fetching tobbar:', error));
  };

  const handleDelete = async (tobbarId) => {
    try {
      const response = await axios.delete(`http://localhost:5678/tobbar/${tobbarId}`);
      console.log('Response:', response.data);
      fetchTobbar();
    } catch (error) {
      console.error('Error deleting tobbar entry:', error);
    }
  };

  const handleFormSubmit = async () => {
    try {
      const newTobbar = { number, location, schoolOpenTiming };
      await axios.post('http://localhost:5678/tobbar', newTobbar);
      
      clearForm();
      fetchTobbar();
    } catch (error) {
      console.error('Error submitting form:', error);
    }
  };

  const handleEditEntry = async (entryId) => {
    try {
      const response = await axios.get(`http://localhost:5678/tobbar/${entryId}`);
      const { number, location, schoolOpenTiming } = response.data;
      setNumber(number);
      setLocation(location);
      setSchoolOpenTiming(schoolOpenTiming);
      setEditingEntryId(entryId);
    } catch (error) {
      console.error('Error fetching tobbar entry for editing:', error);
    }
  };

  const clearForm = () => {
    setNumber('');
    setLocation('');
    setSchoolOpenTiming('');
    setEditingEntryId(null);
  };

  return (
    <Container>
      <Paper elevation={3} style={{ padding: '20px', margin: '20px 0' }}>
        <Typography variant="h4" gutterBottom>
          Tobbar Management
        </Typography>
        <form>
          {/* Form fields */}
        </form>

        <Typography variant="h5" gutterBottom style={{ marginTop: '20px' }}>
          Tobbar Entries
        </Typography>

        <Grid container spacing={2}>
          {tobbar.map(entry => (
            <Grid item xs={12} md={4} key={entry._id}>
              <Card>
                <CardContent>
                  <Typography variant="body2" color="textSecondary">Number: {entry.number}</Typography>
                  <Typography variant="body2" color="textSecondary">Location: {entry.location}</Typography>
                  <Typography variant="body2" color="textSecondary">School Open Timing: {entry.schoolOpenTiming}</Typography>
                  <Button variant="outlined" color="secondary" onClick={() => handleDelete(entry._id)}>
                    Delete
                  </Button>
                  <Button variant="outlined" color="primary" onClick={() => handleEditEntry(entry._id)}>
                    Edit
                  </Button>
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
      </Paper>
    </Container>
  );
};

export default Tobbar;
