import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
  Button,
  TextField,
  TextareaAutosize,
  Container,
  Typography,
  Grid,
  Paper,
  Select,
  MenuItem,
  CardMedia
} from '@material-ui/core';

const Class = () => {
  const [ name, setName] = useState('');
  const [age, setAge] = useState('');
  const [gameName, setGameName] = useState('');
  const [price, setPrice] = useState('');
  const [staffName, setStaffName] = useState('');
  const [imageUrl, setImageUrl] = useState(null);
  const [events, setEvents] = useState([]);
  const [editingEventId, setEditingEventId] = useState(null);
  useEffect(() => {
    fetchEvents();
  }, []);

  const fetchEvents = () => {
    axios.get('http://localhost:5678/events') // Replace with your actual backend endpoint
      .then(response => setEvents(response.data))
      .catch(error => console.error('Error fetching events:', error));
  };

  const handleFileChange = (e) => {
    setImageUrl(e.target.files[0]);
  };

  const handleFormSubmit = async () => {
    try {
      const formData = new FormData();
      formData.append('eventName', eventName);
      formData.append('age', age);
      formData.append('gamesName', gamesName);
      formData.append('price', price);
      formData.append('staffName', staffName);
      formData.append('imageUrl', imageUrl);

      if (editingEventId) {
        // If editing, send a request to update the existing event
        await axios.put(`http://localhost:5678/events/${editingEventId}`, formData);
        setEditingEventId(null); // Reset editing state
      } else {
        // If not editing, send a request to create a new event
        await axios.post('http://localhost:5678/events', formData);
      }

      clearForm();
      fetchEvents();
    } catch (error) {
      console.error('Error submitting form:', error);
    }
  };

 
  
  const handleEditEvent = (eventId) => {
    const eventToEdit = events.find(event => event._id === eventId);
    if (eventToEdit) {
      setEventName(eventToEdit.eventName);
      setAge(eventToEdit.age);
      setGamesName(eventToEdit.gamesName);
      setPrice(eventToEdit.price);
      setStaffName(eventToEdit.staffName);
      setImageUrl(eventToEdit.imageUrl);
      setEditingEventId(eventToEdit._id); // Set the event ID being edited
    }
  };
  
  const handleDeleteEvent = async (eventId) => {
    try {
      await axios.delete(`http://localhost:5678/events/${eventId}`);
      fetchEvents();
    } catch (error) {
      console.error('Error deleting event:', error);
    }
  }
  const clearForm = () => {
    setEventName('');
    setAge('');
    setGamesName('');
    setPrice('');
    setStaffName('');
    setImageUrl(null);
    setEditingEventId(null);
  };

  return (
    <Container>
      <Paper elevation={3} style={{ padding: '20px', margin: '20px 0' }}>
        <Typography variant="h4" gutterBottom>
          Event Admin Panel
        </Typography>
        <form method="post" action="/your-upload-endpoint" enctype="multipart/form-data">
          <Grid container spacing={2}>
            <Grid item xs={12} md={6}>
              <TextField
                label="Event Name"
                variant="outlined"
                fullWidth
                value={eventName}
                onChange={(e) => setEventName(e.target.value)}
              />
            </Grid>
            <Grid item xs={12} md={6}>
              <TextField
                label="Age"
                variant="outlined"
                fullWidth
                value={age}
                onChange={(e) => setAge(e.target.value)}
              />
            </Grid>
            <Grid item xs={12} md={6}>
              <TextField
                label="Games Name"
                variant="outlined"
                fullWidth
                value={gamesName}
                onChange={(e) => setGamesName(e.target.value)}
              />
            </Grid>
            <Grid item xs={12} md={6}>
              <TextField
                label="Price"
                variant="outlined"
                fullWidth
                value={price}
                onChange={(e) => setPrice(e.target.value)}
              />
            </Grid>
            <Grid item xs={12} md={6}>
              <TextField
                label="Staff Name"
                variant="outlined"
                fullWidth
                value={staffName}
                onChange={(e) => setStaffName(e.target.value)}
              />
            </Grid>
            <Grid item xs={12}>
              <input type="file" accept="image/*" onChange={handleFileChange} />
            </Grid>
            <Grid item xs={12}>
              <Button variant="contained" color="primary" onClick={handleFormSubmit}>
                Add Event
              </Button>
            </Grid>
          </Grid>
        </form>
      </Paper>
      {/* Display existing events */}
      <Typography variant="h5" gutterBottom style={{ marginTop: '20px' }}>
        Existing Events
      </Typography>
      <Grid container spacing={2}>
        {events.map(event => (
          <Grid item xs={12} sm={6} md={4} key={event._id}>
            <Paper elevation={3} style={{ padding: '10px', margin: '10px 0' }}>
              <Typography variant="h6" gutterBottom>
                {event.eventName}
              </Typography>
              <Typography variant="h6" gutterBottom>
                {event.age}
              </Typography>
              <Typography variant="h6" gutterBottom>
                {event.gamesName}
              </Typography>
              <Typography variant="h6" gutterBottom>
                {event.price}
              </Typography>
              <Typography variant="h6" gutterBottom>
                {event.staffName}
              </Typography>
              <CardMedia
                component="img"
                alt={event.eventName}
                height="200px"
                image={`http://localhost:5678/${event.imageUrl}`}
              />
              <Button
                variant="contained"
                color="secondary"
                onClick={() => handleDeleteEvent(event._id)}
                style={{ marginTop: '10px' }}
              >
                Delete
              </Button>
              <Button
                variant="contained"
                color="primary"
                onClick={() => handleEditEvent(event._id)}
                style={{ marginTop: '10px', marginLeft: '10px' }}
              >
                Edit
              </Button>
            </Paper>
          </Grid>
        ))}
      </Grid>
    </Container>
  );
};
  
export default Class;
