import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
  Button,
  TextField,
  Container,
  Typography,
  Grid,
  List,
  ListItem,
  ListItemText,
  ListItemSecondaryAction,
  IconButton,
  Divider,
} from '@material-ui/core';
import DeleteIcon from '@material-ui/icons/Delete';
import EditIcon from '@material-ui/icons/Edit';

const Newsletter = () => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [file, setFile] = useState(null);
  const [uploadedDetails, setUploadedDetails] = useState([]);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    try {
      const formData = new FormData();
      formData.append('title', title);
      formData.append('description', description);
      formData.append('pdf', file);
      await axios.post('http://localhost:5678/newsletter/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      alert('PDF uploaded successfully!');
      // Clear form fields after successful upload
      setTitle('');
      setDescription('');
      setFile(null);
      // Refresh uploaded details after successful upload
      fetchUploadedDetails();
    } catch (error) {
      console.error('Error uploading PDF:', error);
      alert('Failed to upload PDF.');
    }
  };

  const fetchUploadedDetails = async () => {
    try {
      const response = await axios.get('http://localhost:5678/newsletter');
      setUploadedDetails(response.data);
    } catch (error) {
      console.error('Error fetching uploaded details:', error);
    }
  };

  useEffect(() => {
    fetchUploadedDetails();
  }, []); // Fetch uploaded details when component mounts

  const handleDeletePDF = async (id) => {
    try {
      await axios.delete(`http://localhost:5678/newsletter/${id}`);
      fetchUploadedDetails();
    } catch (error) {
      console.error('Error deleting PDF:', error);
    }
  };

  const handleEditPDF = (id) => {
    // Add logic to handle editing the PDF
    console.log('Edit PDF with ID:', id);
  };

  return (
    <Container>
      <Typography variant="h4" gutterBottom>
        Upload PDF
      </Typography>
      <form onSubmit={handleFormSubmit}>
        <Grid container spacing={2}>
          <Grid item xs={12}>
            <TextField
              label="Title"
              variant="outlined"
              fullWidth
              value={title}
              onChange={(e) => setTitle(e.target.value)}
            />
          </Grid>
          <Grid item xs={12}>
            <TextField
              label="Description"
              variant="outlined"
              fullWidth
              value={description}
              onChange={(e) => setDescription(e.target.value)}
            />
          </Grid>
          <Grid item xs={12}>
            <input type="file" accept=".pdf" onChange={handleFileChange} />
          </Grid>
          <Grid item xs={12}>
            <Button type="submit" variant="contained" color="primary">
              Upload
            </Button>
          </Grid>
        </Grid>
      </form>
      
      <Typography variant="h5" gutterBottom style={{ marginTop: '20px' }}>
        Uploaded PDFs
      </Typography>
      <List>
        {uploadedDetails.map((detail, index) => (
          <React.Fragment key={index}>
            <ListItem>
              <ListItemText
                primary={detail.title}
                secondary={detail.description}
              />
              <ListItemText primary={detail.filename} />
              <ListItemSecondaryAction>
                <IconButton edge="end" aria-label="delete" onClick={() => handleDeletePDF(detail._id)}>
                  <DeleteIcon />
                </IconButton>
                <IconButton edge="end" aria-label="edit" onClick={() => handleEditPDF(detail._id)}>
                  <EditIcon />
                </IconButton>
              </ListItemSecondaryAction>
            </ListItem>
            <Divider />
          </React.Fragment>
        ))}
      </List>
    </Container>
  );
};

export default Newsletter;
