// App.js
import React, { useEffect, useState } from 'react';
import { useSelector } from 'react-redux';
import { IntlProvider } from 'react-intl';
import { jssPreset, StylesProvider, ThemeProvider } from '@material-ui/core/styles';
import CssBaseline from '@material-ui/core/CssBaseline';
import StyledEngineProvider from '@material-ui/core/StyledEngineProvider';
import { BrowserRouter as Router, Route, Switch, useHistory } from 'react-router-dom';
import LoginPage from './views/login/Login';
import theme from './themes';
import Routes from './routes';
import Snackbar from './ui-component/extended/Snackbar';
import NavigationScroll from './layout/NavigationScroll';
import Dashboard from './views/dashboard/Default';
import rtlPlugin from 'stylis-plugin-rtl';
import { CacheProvider } from '@emotion/react';
import createCache from '@emotion/cache';

import { create } from 'jss';
import rtl from 'jss-rtl';

const jss = create({
  plugins: [...jssPreset().plugins, rtl()],
});

const cacheRtl = createCache({
  key: 'muirtl',
  stylisPlugins: [rtlPlugin],
});

const loadLocaleData = (locale) => {
  switch (locale) {
    default:
      return import('./utils/locals/en.json');
  }
};

const App = () => {
  const [isLoggedIn, setLoggedIn] = useState(false);
  const history = useHistory(); // Initialize useHistory

  const handleLogin = () => {
    // Perform authentication logic here (e.g., API call, validation)
    // For simplicity, we'll set isLoggedIn to true immediately upon login.
    setLoggedIn(true);

    // Show a popup or alert for successful login
    alert('Login successful!');

    // Use history.push after the state is updated to navigate to the dashboard
    history.push('/dashboard/default');
  };

  // ... (rest of the component)

  return (
    <React.Fragment>
      <StylesProvider jss={jss}>
        <CacheProvider value={cacheRtl}>
          {messages && (
            <IntlProvider locale={customization.locale} defaultLocale="en" messages={messages}>
              <StyledEngineProvider injectFirst>
                <NavigationScroll>
                  <ThemeProvider theme={theme(customization)}>
                    <CssBaseline />

                    <Routes />
                    <Switch>
                      <Route path="/login">
                        <LoginPage onLogin={handleLogin} />
                      </Route>
                      <Route path="/dashboard/default">
                        {isLoggedIn ? <Dashboard /> : <Redirect to="/login" />}
                      </Route>
                      <Redirect from="*" to="/login" />
                    </Switch>
                    <Snackbar />
                  </ThemeProvider>
                </NavigationScroll>
              </StyledEngineProvider>
            </IntlProvider>
          )}
        </CacheProvider>
      </StylesProvider>
    </React.Fragment>
  );
};

export default App;
