import React, { useState, useEffect } from 'react';
import { Grid, Typography } from '@material-ui/core';
import { gridSpacing } from '../../store/constant';
import Admission from '../admission/Admission.jsx';
import TotalChartCard from '../../ui-component/cards/TotalChartCard/index.js';

const Dashboard = () => {
  const [greeting, setGreeting] = useState('');

  useEffect(() => {
    const currentHour = new Date().getHours();
    setGreeting(getGreeting(currentHour));
    console.log(window.innerWidth )
  }, []);

  const getGreeting = (hour) => {
    if (hour >= 5 && hour < 12) {
      return 'Good Morning Admin';
    } else if (hour >= 12 && hour < 18) {
      return 'Good Afternoon Admin';
    } else {
      return 'Good Evening Admin';
    }
  };

  const calculateStyles = () => {
    const isSmallScreen = window.innerWidth <= 957;
    const isMediumScreen = window.innerWidth >= 700;

    if (isSmallScreen) {
      return {
        height: "100%",
        width: "100%",
        backgroundColor: "white",
        position: "absolute",
        zIndex: "1000",
        top: "0px",
        marginLeft: "-280px"
      };
    } else if (isMediumScreen) {
      return {
        height: "100%",
        width: "100%",
        backgroundColor: "white",
        position: "absolute",
        zIndex: "1000",
        top: "0px",
        left: "0px"
      };
    } else {
      return {
        height: "100%",
        width: "100%",
        backgroundColor: "white",
        position: "absolute",
        zIndex: "1000",
        top: "80px",
        left: "280px"
      };
    }
  };

  return (
    <Grid container style={calculateStyles()}>
      <Grid item xs={12}>
        <Typography variant="h1" gutterBottom>
          {greeting}
        </Typography>
        <TotalChartCard/>
      </Grid>
    </Grid>
  );
};

export default Dashboard;
